        total_volume = merchant_txs['eur_amount'].sum()
        fraud_volume = merchant_txs[merchant_txs['has_fraudulent_dispute'] == True]['eur_amount'].sum()

        if total_volume > 0:
            fraud_rate = fraud_volume / total_volume
