# Helper functions for robust data processing
def coerce_to_float(value):
    """Convert string with %, $, commas to float. Handles common formats."""
    if isinstance(value, (int, float)):
        return float(value)
    if isinstance(value, str):
        v = value.strip().replace(',', '').replace('€', '').replace('$', '')
        v = v.lstrip('><≤≥')  # Remove comparison operators
        if '%' in v:
            return float(v.replace('%', '')) / 100
        # Range handling (e.g., "50-60") - return mean
        if '-' in v and len(v.split('-')) == 2:
            try:
                parts = v.split('-')
                return (float(parts[0]) + float(parts[1])) / 2
            except:
                pass
        return float(v)
    return float(value)

def safe_get(df, column, default=None):
    """Safely get column from DataFrame, return default if not exists."""
    if isinstance(df, dict):
        return df.get(column, default)
    elif hasattr(df, 'columns') and column in df.columns:
        return df[column]
    return default

def is_not_empty(array):
    """Check if array/list is not empty. Handles numpy arrays safely."""
    if array is None:
        return False
    if hasattr(array, 'size'):  # numpy array
        return array.size > 0
    try:
        return len(array) > 0
    except TypeError:
        return False

def safe_index(array, idx, default=None):
    """Safely get array element at index, return default if out of bounds."""
    try:
        if 0 <= idx < len(array):
            return array[idx]
        return default
    except (IndexError, TypeError, AttributeError):
        return default


import json

# Path to the fees file
fees_file_path = '/output/chunk2/data/context/fees.json'

# Load the fees data
try:
    with open(fees_file_path, 'r') as f:
        fees_data = json.load(f)

    # Search for the rule with ID=398
    target_rule = None
    for rule in fees_data:
        if rule.get('ID') == 398:
            target_rule = rule
            break

    if target_rule:
        print(f"Successfully extracted Fee Rule ID: {target_rule['ID']}")
        print(f"Original Rate: {target_rule['rate']}")
        print("Full Rule Details:")
        print(json.dumps(target_rule, indent=4))
    else:
        print("Error: Fee rule with ID=398 not found in the dataset.")

except FileNotFoundError:
    print(f"Error: File not found at {fees_file_path}")
except json.JSONDecodeError:
    print(f"Error: Failed to decode JSON from {fees_file_path}")
except Exception as e:
    print(f"An unexpected error occurred: {e}")
